// Define the PDF content
const docDefinition = {
  content: [
    "This is an example of pdfmake",
    {
      text: "This is a header",
      style: "header",
    },
    "Some more text",
  ],
  styles: {
    header: {
      fontSize: 18,
      bold: true,
    },
  },
};

// Generate the PDF and display it in the iframe
pdfMake.createPdf(docDefinition).getDataUrl(function (url) {
  document.getElementById("pdfIframe").src = url;
});
